const dayjs = require('dayjs');
// require() is a built-in function in Node.js 
// that you can use to include external modules in your code.
// require() refers node_modules folder in the root directory automatically
console.log(dayjs('1919-10-07').format('YYYY-MM-DD'));
console.log(dayjs().format('YYYY-MM-DD'));
// dayjs() => current date and time

